const input = $('Called from WF4').first().json;
const lead = $('Get Lead State').first().json;

const firstName = (lead.fullName || '').split(' ')[0];

return [{ json: {
  chatInput: `Rejection reason: ${input.rejectionReason}\nCustom note from admin: ${input.customNote || ''}\nClient name: ${firstName}\nFinancial goals: ${lead.financialGoals || ''}\nAsset range: ${lead.investableAssets || ''}`,
  leadId: input.leadId,
  email: lead.email,
  fullName: lead.fullName,
  firstName,
  rejectionReason: input.rejectionReason,
  customNote: input.customNote || '',
  financialGoals: lead.financialGoals || '',
  investableAssets: lead.investableAssets || ''
} }];

